content.value:
.grid {
  width: 80%;
  margin: 0 auto;

  display:  grid;
  grid-gap: 20px 30px;
  grid-template-areas:
    "header header header"
    "banner banner banner"
    "content content sidebar"
    "footer footer footer";
}

.grid__item {
  background-color: #eee;

  &amp;--header {
    grid-area: header;
    padding: 20px;
  }

  &--banner {
    grid-area: banner;
    padding: 60px 20px;
  }

  &--content {
    grid-area: content;
    padding: 100px 20px;
  }

  &--sidebar {
    grid-area: sidebar;
    padding: 100px 20px;
  }

  &--footer {
    grid-area: footer;
    padding: 20px;
  }
}
